package slideWin;

import org.omg.CORBA.INTERNAL;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

/**
 * https://leetcode-cn.com/problems/find-all-anagrams-in-a-string/
 * 438. 找到字符串中所有字母异位词
 * 给定一个字符串 s 和一个非空字符串 p，找到 s 中所有是 p 的字母异位词的子串，返回这些子串的起始索引。
 *
 * 字符串只包含小写英文字母，并且字符串 s 和 p 的长度都不超过 20100。
 *
 * 说明：
 *
 * 字母异位词指字母相同，但排列不同的字符串。
 * 不考虑答案输出的顺序。
 * 示例 1:
 *
 * 输入:
 * s: "cbaebabacd" p: "abc"
 *
 * 输出:
 * [0, 6]
 *
 * 解释:
 * 起始索引等于 0 的子串是 "cba", 它是 "abc" 的字母异位词。
 * 起始索引等于 6 的子串是 "bac", 它是 "abc" 的字母异位词。
 *  示例 2:
 *
 * 输入:
 * s: "abab" p: "ab"
 *
 * 输出:
 * [0, 1, 2]
 */
public class FindAnagrams {
    public static void main(String[] args) {
//        List<Integer> ans = (new FindAnagrams()).findAnagrams("cbaebabacd", "abc");
        List<Integer> ans = (new FindAnagrams()).findAnagrams("", "");
        System.out.println();
    }
    public List<Integer> findAnagrams(String s, String p) {
        int slen = s.length();
        int plen = p.length();
        if (slen == 0 || plen > slen) {
            return new ArrayList<>();
        }
        List<Integer> ans = new ArrayList<>();
        int left = 0;
        int right = 0;
        int match = 0;
        HashMap<Character, Integer> needs = new HashMap<>();
        for (int i = 0; i <plen; i++) {
            needs.put(p.charAt(i), needs.getOrDefault(p.charAt(i), 0 ) +1);
        }
        HashMap<Character, Integer> win = new HashMap<>();
        while (right < slen) {
            char curr = s.charAt(right);
            if (!needs.containsKey(curr)) {
                win = new HashMap<>();
                left = right += 1;
                match = 0;
                continue;
            }
            win.put(curr, win.getOrDefault(curr, 0) + 1);

            if (needs.containsKey(curr) && needs.get(curr).equals(win.get(curr))) {

                match += 1;
            }
            //满足条件的跳出
            if (match == needs.size()) {
                ans.add(left);
                char currLfet = s.charAt(left);
                win.put(currLfet, win.get(currLfet) - 1);
                match--;
                left++;
            }
            //开始缩小
            while (win.get(curr) > needs.get(curr)) {
                char currLfet = s.charAt(left);
                win.put(currLfet, win.get(currLfet) - 1);
                if (win.get(currLfet) < needs.get(currLfet)) {
                    match--;
                }
                left++;
            }
            right++;
        }
        return ans;
    }
}
